import React, { Component } from "react";
import { useState, useEffect } from "react";
import { randomWord } from "./words";
// import { QiRoboService } from '../services/QIService';
import words from "./words";


import step0 from "./images/0.jpg";
import step1 from "./images/1.jpg";
import step2 from "./images/2.jpg";
import step3 from "./images/3.jpg";
import step4 from "./images/4.jpg";
import step5 from "./images/5.jpg";
import step6 from "./images/6.jpg";

const handleDialogue = (text) => {
  QiRoboService.onService("ALTextToSpeech", (ALTextToSpeech) => {
    ALTextToSpeech.say(text);
  });
}

const Hangman = () => {
  const [mistake, setMistake] = useState(0);
  const [guessed, setGuessed] = useState(new Set());
  const [answer, setAnswer] = useState(randomWord().word);
  const [hint, setHint] = useState(randomWord().hint);

  useEffect(() => {
    window.addEventListener("keydown", keyPress);
    return () => {
      window.removeEventListener("keydown", keyPress);
    };
  }, []);

  useEffect(() => {
    handleDialogue(answer);
  }, [answer]);

  const guessedWord = () => {
    return answer.split("").map((bingo) => (guessed.has(bingo) ? bingo : "_"));
  };

  const handleGuess = (value) => {
    let letter = value;
    handleDialogue(letter);
    setGuessed((prevGuessed) => new Set(prevGuessed.add(letter)));
    setMistake((prevMistake) =>
      prevMistake + (answer.includes(letter) ? 0 : 1)
    );
  };
  const keyPress = (event) => {
    if (
      (event.keyCode >= 65 && event.keyCode <= 90) ||
      (event.keyCode >= 97 && event.keyCode <= 122)
    ) {
      handleGuess(event.key);
    } else if (
      event.keyCode === 8 ||
      event.keyCode === 13 ||
      event.keyCode === 32
    ) {
      resetButton();
    }
  };


  const generateButtons = () => {
    return "abcdefghijklmnopqrstuvwxyz".split("").map((letter) => (
      <button
        key={letter}
        value={letter}
        onClick={(e) => handleGuess(e.target.value)}
        disabled={guessed.has(letter)}
      >
        {letter}
      </button>
    ));
  };

  const resetButton = () => {
    setMistake(0);
    setGuessed(new Set());
    setAnswer(randomWord().word);
    setHint(randomWord().hint);
  };

  const maxWrong = 6;
  const images = [step0, step1, step2, step3, step4, step5, step6];
  const altText = `${mistake}/${maxWrong} wrong guesses`;
  const isWinner = guessedWord().join("") === answer;
  let gameStat = generateButtons();
  const gameOver = mistake >= maxWrong;

  if (isWinner) {
    gameStat = "YOU WON";
    handleDialogue("Congratulations. You guessed it right.");
  }

  if (mistake >= maxWrong) {
    gameStat = "YOU LOST";
    handleDialogue("Oops! No problem. Better Luck Next Time!");
  }

  return (
    <div style={{ display: "flex", flexDirection: "column" }}>
      <div
        className="Hangman"
        style={{ display: "flex", flexDirection: "column", alignItems: "center" }}
      >
                <nav style={{ display: 'flex', flexDirection: 'row' }}>
          <span style={{ color: 'blue' }}>
            Guessed wrong: {mistake}
          </span>
          <button
            style={{ display: 'none' }}
            type="button"
            data-toggle="collapse"
            data-target="#navbarText"
            aria-controls="navbarText"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span></span>
          </button>
          <div style={{ display: 'none' }} id="navbarText">
            <ul style={{ display: 'flex', flexDirection: 'row' }}>
              <li></li>
              <li></li>
              <li></li>
            </ul>
            <span style={{ color: 'blue' }}>
              Guessed wrong: {mistake}
            </span>
          </div>
        </nav>
        <p style={{ textAlign: 'center' }}>
          <img src={images[mistake]} alt={altText} />
        </p>
        <p style={{ textAlign: 'center', color: 'white', fontSize: '20px' }}>
          {hint}
        </p>
        <p className="Hangman-word" style={{ textAlign: 'center' }}>
          {!gameOver ? guessedWord() : answer}
        </p>
        <p style={{ textAlign: 'center', color: 'yellow', marginTop: '4px' }}>
          {gameStat}
        </p>
        <div>
          <p style={{ textAlign: 'center' }}>
            <button className="Hangman-reset" onClick={resetButton}>
              Reset
            </button>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Hangman;
