import React, { Component } from "react";
import { useState, useEffect } from "react";
import { randomWord } from "./words";
import { QiRoboService } from '../services/QIService';
import words from "./words";


import step0 from "./images/0.jpg";
import step1 from "./images/1.jpg";
import step2 from "./images/2.jpg";
import step3 from "./images/3.jpg";
import step4 from "./images/4.jpg";
import step5 from "./images/5.jpg";
import step6 from "./images/6.jpg";

const handleDialogue = (text) => {
  QiRoboService.onService("ALTextToSpeech", (ALTextToSpeech) => {
    ALTextToSpeech.say(text);
  });
}

let gameStat;
class Hangman extends Component {
  static defaultProps = {
    maxWrong: 6,
    images: [step0, step1, step2, step3, step4, step5, step6],
  };

  constructor(props) {
    super(props);
    this.state = {
      mistake: 0,
      guessed: new Set(),
      answer: words[0].word,
      hint: randomWord().hint
    };
    this.handleGuess = this.handleGuess.bind(this);
    this.keyPress = this.keyPress.bind(this);
    window.addEventListener("keydown", this.keyPress);
  }

  guessedWord() {
    return this.state.answer
      .split("")
      .map((bingo) => (this.state.guessed.has(bingo) ? bingo : "_"));
  }

  handleGuess(value) {
    let letter = value;
    handleDialogue(letter);
    this.setState((st) => ({
      guessed: st.guessed.add(letter),
      mistake: st.mistake + (st.answer.includes(letter) ? 0 : 1),
    }));
  }

  keyPress(event) {
    if (gameStat === "YOU WON" || gameStat === "YOU LOST") {
      if (event.keyCode === 8 || event.keyCode === 13 || event.keyCode === 32) {
        this.resetButton();
      }
    } else if (
      (event.keyCode >= 65 && event.keyCode <= 90) ||
      (event.keyCode >= 97 && event.keyCode <= 122)
    ) {
      this.handleGuess(event.key);
    } else if (
      event.keyCode === 8 ||
      event.keyCode === 13 ||
      event.keyCode === 32
    ) {
      this.resetButton();
    } else {
    }
  }

  generateButtons() {
    return "abcdefghijklmnopqrstuvwxyz".split("").map((letter) => (
      <button
        key={letter}
        value={letter}
        onClick={(e) => this.handleGuess(e.target.value)}
        disabled={this.state.guessed.has(letter)}
      >
        {letter}
      </button>
    ));
  }

  resetButton = () => {
    this.setState({
      mistake: 0,
      guessed: new Set(),
      answer: randomWord().word,
      hint: randomWord().hint
    });
  };

  render() {
    const { mistake, answer } = this.state;
    const { maxWrong, images } = this.props;
    const gameOver = mistake >= maxWrong;
    
    const altText = `${mistake}/${maxWrong} wrong guesses`;
    const isWinner = this.guessedWord().join("") === answer;
    gameStat = this.generateButtons();
    
     
     if (isWinner) {
      gameStat = "YOU WON";
      handleDialogue("Congratulations. You guessed it right.")
    }
    if (gameOver) {
      gameStat = "YOU LOST";
      handleDialogue("Oops!No problem.Better Luck Next Time! " )
    }

    return (
      <div style={{ display: 'flex', flexDirection: 'column' }}>
  <div className="Hangman" style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
    <nav style={{ display: 'flex', flexDirection: 'row' }}>
      <span style={{ color: 'blue' }}>
        Guessed wrong: {mistake}
      </span>
      <button
        style={{ display: 'none' }}
        type="button"
        data-toggle="collapse"
        data-target="#navbarText"
        aria-controls="navbarText"
        aria-expanded="false"
        aria-label="Toggle navigation"
      >
        <span></span>
      </button>
      <div style={{ display: 'none' }} id="navbarText">
        <ul style={{ display: 'flex', flexDirection: 'row' }}>
          <li></li>
          <li></li>
          <li></li>
        </ul>
        <span style={{ color: 'blue' }}>
          Guessed wrong: {mistake}
        </span>
      </div>
    </nav>
    <p style={{ textAlign: 'center' }}>
      <img src={images[mistake]} alt={altText} />
    </p>
    <p style={{ textAlign: 'center', color: 'white', fontSize: '20px' }}>
      {this.state.hint}
    </p>
    <p className="Hangman-word" style={{ textAlign: 'center' }}>
      {!gameOver ? this.guessedWord() : answer}
    </p>
    <p style={{ textAlign: 'center', color: 'yellow', marginTop: '4px' }}>
      {gameStat}
    </p>
    <div>
      <p style={{ textAlign: 'center' }}>
        <button className="Hangman-reset" onClick={this.resetButton}>
          Reset
        </button>
      </p>
    </div>
  </div>
</div>

    );
  }
}

export default Hangman;
